definition.script:
pipeline {
    agent {
        label 'windows'
    }
    tools {
        jdk 'jdk25'
        maven 'mvn'
    }
    stages {
        stage('Checkout') {
            steps {
                checkout scmGit(branches: [[name: 'master']],
                                extensions: [cloneOption(depth: 1, honorRefspec: true, noTags: true, shallow: true)],
                                gitTool: 'Default',
                                userRemoteConfigs: [[refspec: '+refs/heads/master:refs/remotes/origin/master',
                                                     url: 'https://github.com/jenkinsci/jenkins.git']])
            }
        }
        stage('Test') {
            steps {
                bat 'mvn -am -pl war,bom -Pquick-build clean install'
                bat 'mvn -pl test -Dtest=Security914Test -Dmaven.test.failure.ignore=true test'
                junit '**/surefire-reports/*.xml'
            }
        }
    }
}
