definition.script:
node ("Unix && git-2.11+") {
  // Mark the code checkout 'stage'....
  stage('Checkout') {
      deleteDir()
     // Get some code from a GitHub repository
     checkout([$class: 'GitSCM', branches: [[name: '*/master']],
         extensions: [[$class: 'CloneOption', noTags: true, reference: '/var/cache/git/mwaite/jenkins/git-plugin.git', timeout: 1],
             [$class: 'CleanBeforeCheckout']
             ],
         gitTool: 'Default',
         userRemoteConfigs: [[url: 'https://github.com/jenkinsci/git-plugin.git']]])
  }

  stage('Test') {
    // Verify objects directory is small
    sh "if [ `du -s .git/objects/ | awk '{print \$1}'` -lt 1000 ]; then echo objects less than 1000, test passed; ls -altr .git/objects/*; else echo objects too large; ls -altr .git/objects/; exit 1; fi"
  }
}
