definition.script:
def gitVars = null

node {
   stage('Prep') {
       gitVars = checkout([$class: 'GitSCM',
              branches: [[name: 'master']],
              extensions:
                [[$class: 'AuthorInChangelog'],
                 [$class: 'LocalBranch', localBranch: '**'],
                 [$class: 'CloneOption', honorRefspec: true, noTags: true, reference: '/var/lib/git/mwaite/bugs/jenkins-bugs.git'],
                 [$class: 'CleanBeforeCheckout']],
              userRemoteConfigs:
                [[refspec: '+refs/heads/master:refs/remotes/origin/master',
                  url: 'https://github.com/MarkEWaite/jenkins-bugs']]])
   }
   stage('Build') {
      if (isUnix()) {
         sh "env | sort"
      } else {
         bat("set")
      }
      def output = "*** Map returned from checkout ***:\n"
      for (gitEnv in gitVars) {
          output = output + "${gitEnv.key}=${gitEnv.value}\n"
      }
      echo output
   }
   stage('Verify') {
      // Log should contain GIT_COMMIT=
      if (!manager.logContains(".*GIT_COMMIT=.*")) {
          manager.addWarningBadge("No GIT_COMMIT env variable")
          manager.createSummary("warning.gif").appendText(&quot# No GIT_COMITT env variable!", false, false, false, "yellow")
          manager.buildUnstable()
      }
   }
}
