definition.script:
pipeline {
    agent any
    stages {
        stage('Checkout') {
            steps {
                checkout scmGit(extensions: [[ $class: 'PreBuildMerge',
                                               options: [
                                                   mergeRemote: 'origin',
                                                   mergeTarget: 'master'
                                               ]
                                            ]],
                                userRemoteConfigs: [[url: 'https://github.com/jenkinsci/xshell-plugin.git']])
                findText(textFinders: [textFinder(alsoCheckConsoleOutput: true,
                                                  buildResult: 'UNSTABLE',
                                                  changeCondition: 'MATCH_NOT_FOUND',
                                                  regexp: 'DEPRECATED: The .Merge before build. extension is deprecated for Pipeline jobs.')])
            }
        }
    }
}
