definition.script:
properties( [ pipelineTriggers([cron('@daily')]), buildDiscarder(logRotator(numToKeepStr: '7')) ] )
node('(!windows && !cloud) || built-in || master') { stage('create dirs') { deleteDir() echo """ **** **** **** Create test directories **** **** **** """ sh ("mkdir -p parent/foo && touch parent/foo/file-1") sh ("mkdir -p bar/parent && touch bar/parent/file-2") sh ("mkdir parent-foo && touch parent-foo/file-3")
// Report the URL's for interactive testing echo """ **** **** **** Test URLs for interactive checks **** **** **** """ echo "OK URL is ${env.BUILD_URL}/execution/node/4/ws/" echo "Odd URL is ${env.BUILD_URL}/execution/node/4/ws/parent/" echo "Broken URL is ${env.BUILD_URL}/execution/node/4/ws/parent/foo"
// Open the URL for automated verification echo """ **** **** **** Automated checks with curl of the test URLs **** **** **** """ sh "curl -Is ${env.BUILD_URL}/execution/node/4/ws/ | grep ^HTTP.*200.OK" sh "curl -Is ${env.BUILD_URL}/execution/node/4/ws/parent/ | grep ^HTTP.*200.OK"
echo """ **** **** **** Marking build as unstable if 404 Not Found is detected in output **** **** **** """ grep404NotFoundReturnCode = sh returnStatus: true, script: "curl -Is ${env.BUILD_URL}/execution/node/4/ws/parent/foo | grep ^HTTP.*404.Not.Found" if (grep404NotFoundReturnCode == 0) { currentBuild.result = 'UNSTABLE' } } }
