definition.script:
pipeline {
  agent any
  parameters {
    gitParameter type: 'PT_BRANCH',
                 name: 'A_BRANCH',
                 branchFilter: 'origin/(.*)',
                 defaultValue: 'master',
                 description: 'Choose a branch to checkout',
                 selectedValue: 'DEFAULT',
                 sortMode: 'DESCENDING_SMART'
  }
  stages {
    stage('Example') {
      steps {
        git branch: params.A_BRANCH,
            url: 'https://github.com/jenkinsci/git-plugin.git'
      }
    }
  }
}
