definition.script:
def nodes = nodesByLabel label: 'windows || !windows'

Map tasks = [:]

for (int i = 0; i < nodes.size(); i++) {
    def label = nodes[i]
    def stageName = 'Git- ' + label.replaceAll('(-jagent)|(-ssh)|(-mwaite)|(.markwaite.net)|(-beemarkwaite)|(-jenkins)', '')
    tasks[label] = {
        node(label) {
            stage(stageName) {
                if (isUnix()) {
                    sh 'git config --system --list'
                    sh 'git config --global --list'
                    sh 'git config          --list'
                } else {
                    bat 'git config --system --list'
                    bat 'git config --global --list'
                    bat 'git config          --list'
                }
            }
            stage('done') {
                echo 'Done stage for graph viewer workaround'
            }
        }
    }
}

timeout(time: 3, unit: 'MINUTES') {
    parallel(tasks)
}
